mapper.select:


SELECT user_id, bot_id, name, val,created
  FROM aiml_predicate
  WHERE 0 = 0
    AND user_id = #{userid}
    AND bot_id = #{botid}
    AND name = #{name};



          SELECT user_id, bot_id, name, val, created
			FROM aiml_predicate
            WHERE 0 = 0
          AND user_id = #{userid}
          AND bot_id = #{botid}
          AND name = #{name}

mapper.delete:
DELETE FROM aiml_predicate
WHERE 0 = 0
  AND user_id = #{userid}
  AND bot_id = #{botid}
  AND name = #{name};

mapper.update:
UPDATE aiml_predicate

    val = #{val}

WHERE 0 = 0
AND user_id = #{userid}
AND bot_id = #{botid}
AND name = #{name};
