mapper.select:
SELECT count(*) FROM deploy_node_history
WHERE 0 = 0
AND scheduler_id = #{schedulerId}
AND write_success = 'Y'
AND read_success = 'Y';


SELECT scheduler_id, host_ip, file_name, created, modified, write_success, read_success, err_msg
FROM deploy_node_history
WHERE scheduler_id = #{schedulerId} AND host_ip = #{hostIp};


SELECT scheduler_id, host_ip, file_name, created, modified, write_success, read_success, err_msg
FROM deploy_node_history
WHERE scheduler_id = #{schedulerId}
offset ${firstRecordIndex} limit ${recordCountPerPage};

mapper.delete:

DELETE FROM deploy_node_history
WHERE scheduler_id = #{schedulerId}
AND host_ip = #{hostIp}
AND file_name = #{fileName};

mapper.update:
UPDATE deploy_node_history


        write_success = #{writeSuccess},


        read_success =  #{readSuccess},

        modified = now()

WHERE scheduler_id = #{schedulerId}
AND host_ip = #{hostIp}
AND file_name = #{fileName};
